	part[1]=1;
	int k;
	For(i,2,N){
		if(!vis[i]){//素数 
			f[i]=i*(i-1)+1;//素数初值
			p[++p[0]]=i; 
			part[i]=1;//本身就是最小素因子
		}
		For(j,1,p[0]){//枚举最小素因子 
			k=i*p[j];
			if(k>N)break;//>max
			vis[k]=1;//你永远失去了当素数的机会 
			if(i%p[j]){//不整除
				part[k]=i;
				f[k]=f[i]*f[p[j]];
			}else{
				part[k]=part[i];
				if(part[i]>1){//你心里还有别人
					f[k]=f[part[i]]*f[i/part[i]*p[j]]; 
				}else{//你心里没有别人
					f[k]=f[i]+k*(k-k/p[j]);//素数幂次递推 
				}
				break;
			} 
		}
	} 
}
signed main(){
	IOS;
	sieve();
	int T;
	cin>>T;
	while(T--){
		cin>>n;
		cout<<(1+f[n])/2*n<<endl;
	}
    return 0;
}
```

