}
using ae86::read;

inline void euler()
{
	int siz = 1e6 + 1;
	phi[1] = isPrime[1] = 1;
	for (register int i = 2; i <= siz; ++ i) {
		if (!isPrime[i]) {
			prime[++ tot] = i, phi[i] = i - 1;
		}
		for (register int j = 1; j <= tot && i * prime[j] <= siz; ++ j) {
			isPrime[i * prime[j]] = 1;
			if (i % prime[j])  phi[i * prime[j]] = phi[i] * phi[prime[j]];
			else {
