#include<cstdio>
#include<algorithm>
using namespace std;
long long a[31269];
int b[15],maxn;
int lg10(int x)
{
    if(x>=1&&x<=9)
    return 1;
    if(x>=10&&x<=99)
    return 2;
    if(x>=100&x<=999)
    return 3;
    if(x>=1000&&x<=9999)
    return 4;
    return 5;
}
int f(int x)
{
    if(x>=1&&x<=9)
    return x;
    int tmp=0,num=0;
    while(tmp<x)
    {
        num++;
        tmp+=lg10(num);
    }
    if(tmp==x)
    return num%10;
    tmp=tmp-x;
    for(int i=1;i<=tmp;++i)
    {
        num/=10;
    }
    return num%10;
}
int main()
{
    int nn;
    scanf("%d",&nn);
    for(int i=1;i<=nn;++i)
    {
        scanf("%d",b+i);
        maxn=max(maxn,b[i]);
    }
    a[1]=1,a[2]=3;
    int sum=3,num=2;
    while(sum<maxn)
    {
        num++;
        sum+=(a[num-1]-a[num-2])+lg10(num);
        a[num]=sum;
