```cpp 
#include<bits/stdc++.h>
using namespace std;
long long t,n,sum=0,i=0;
int len[100010];
string s;
inline string zs(int a)//把一个 int 数值转换成 string 类 
{
	string s="\0";
	while(a>0)
	{
		s+=a%10+48;
		a/=10;
	}
	for(int i=0; i<s.size()/2; i++)
		swap(s[i],s[s.size()-1-i]);
	return s;
}
int main()
{
	cin>>t;
	//------预处理------// 
	for(int j=1; j<=9; j++)
		len[j]=len[j-1]+1;
	for(int j=10; j<=99; j++) 
		len[j]=len[j-1]+2;
	for(int j=100; j<=999; j++)
		len[j]=len[j-1]+3;
	for(int j=1000; j<=9999; j++) 
		len[j]=len[j-1]+4;
	for(int j=10000; j<=99999; j++) 
		len[j]=len[j-1]+5;

	for(int g=1; g<=t; g++)
	{
		cin>>n;
		s="",sum=0,i=0;//记得把要重复使用的变量清零 
		while(sum<n) 
        		sum+=len[++i];//Step1 
		n-=sum-len[i];//Step2
		i=1;
		while(1)//Step3
		{
			string s2=zs(i);
			s=s+s2;
			if(s.size()>=n)
			{
				cout<<s[n-1]<<"\n";
				break;
			}
			i++;
		}	
	}

	return 0;
}
