```
#include <iostream>
#include <fstream>
#include <stdio.h>
#include <math.h>
#include <algorithm>
#include <string.h>
#define re register
#define gc getchar()
#define Lowbit(X) (X&(-X))
#define int long long
using namespace std ;
int Qread () {
	int X = 0;
	char  ch = gc ;
	while (ch > '9' || ch < '0') ch = gc ;
	while (ch >='0' && ch <='9') {
		X = X * 10 + ch - '0' ;
		ch = gc ;
	}
	return X ;
}
int N ;
const int Maxn = 9 , MaxPw = 512 , Maxk = 81;
int K ,F[Maxn+2][MaxPw+2][Maxk + 2] , Num[MaxPw + 2];
unsigned int Pw;
int BC(int X) {
	int Cnt = 0 ;
	while (X) {
		++Cnt ;
		X -= Lowbit(X) ;
	}
	return Cnt ;
}
signed main () {
	//freopen ("P1896.txt" , "r" , stdin) ;
	N = Qread () , K =  Qread () ;
	Pw = ( 1 << N )- 1 ;
	memset (F , 0 , sizeof (F)) ;
	for (re int i = 0 ; i <= Pw ; ++ i) Num[i] = BC(i) ;

	for (re int i = 0 ; i<=Pw ; ++ i) {
		if (Num[i] <= K) {
			F[0][i][Num[i]] = 1 ;
		}
	}
	for (re int i = 0 ; i < N -1; ++ i) {
		for (re int j = 0 ; j <= Pw ; ++ j) {
			if(Num[j] > K) continue ;
			if (j&(j>>1)) continue ;
			for (re int k = 0 ; k <= K ; ++ k ) {
				if (!F[i][j][k]) continue ;

				for (re int jj = 0 ; jj <= Pw ; ++ jj) {
					if (Num[jj] + k > K) continue ;
					if ((jj&(j>>1))||(jj&(j))||(jj&(j<<1)))continue ;
					F[i+1][jj][Num[jj] + k] += F[i][j][k] ;
				}
			}
		}
	}
	int Ans =  0 ;
	for (re int i = 0 ; i <= Pw ; ++ i) {
		if (i&(i<<1)) continue ;
		Ans += F[N-1][i][K] ;
	}
	cout <<Ans<<endl;
	fclose (stdin) ;
	fclose (stdout);
	return 0;
}
```

