			   f[i][si[j]][ss]+=f[i-1][si[k]][ss-gs[j]];
		}
	    /*常规方案统计一波*/

		inc(i,1,size)
		ans+=f[n][si[i]][king];
	}
	printf("%lld",ans);
	re 0;
}

