    for (int i = 0; i < (1 << N); ++i) {
        if(i & (i << 1))
            continue;
        s[0]++;
        int t = i;
        num[s[0]] = 0;
        while (t){
            num[s[0]] += (t&1);
            t = t >> 1;
        }
        s[s[0]] = i;
    }
    int s1,s2;
    G.clear();
    G.resize(MAXN*10);
    for (int j = 1; j <= s[0]; ++j) {
        for (int i = j+1; i <= s[0]; ++i) {//为什么不从j开始循环,因为0到0时会push两次1-1,会造成错误哒
            s1 = s[j],s2 = s[i];
            if( ((s2 << 1) & s1) || ((s2 >> 1) & s1) || (s1 & s2) )//判断s1状态与s2状态是否冲突
                continue;
            G[i].push_back(j);//状态号
            G[j].push_back(i);
        }
    }
    G[1].push_back(1);//0到0,也就是说可以连续出现两次第一个状态,也就是两行都不放
}
void DP(){
    f[0][1][0] = 1;//f(i,j,k) 考虑前i行,是第k个状态时,共放了j只猛兽//第一个状态就是0000
    int m = 0,s1;
    for (int i = 1; i <= N; ++i) {
        m ^= 1;
        //初始化数组
        for (int j = 1; j <= s[0]; ++j) {
            for (int k = 0; k <= K; ++k) {
                f[m][j][k] = 0;
            }
        }
        for (int j = 1; j <= s[0]; ++j) {
            for (int k = 0; k <= K; ++k) {
                for (int l = 0; l < G[j].size(); ++l) {
                    s1 = G[j][l];
                    if(k - num[j] >= 0){
                        f[m][j][k] += f[m^1][s1][k-num[j]];
                    }                //冻柜sa
                }
            }
        }
    }
    Ans = 0;
    for (int n = 1; n <= s[0]; ++n) {
        Ans += f[m][n][K];
    }
    cout << Ans << endl;
}
int main() {
    init();
    DP();
    return 0;
}
```

