```cpp
#include<iostream>
#include<algorithm>            //sort的头文件，不要忘记 
using namespace std;
int main()
{
	int n;                   //人数 
	cin>>n;
	const int nn=n;
	int a[nn];                //用来存储每个人要去的层数 
	int sum=0;                //总时长 
	int max=-999999999;       //存储最高楼层 
	for(int i=0;i<n;i++)
	{
		cin>>a[i];
		if(a[i]>max)
		{
			max=a[i];
		}
	}
	sort(a,a+nn);             //排序 
	int zs=1;                 //要去的不同层数的数量 
	int k=a[0];
	for(int i=0;i<n-1;i++)     //存储不同层数的数量，因为已经排过序所以直接顺着来一遍就行 
	{
		if(a[i+1]!=k)
		{
			zs++;
			k=a[i+1];
		}
	}
	sum=max*10+zs*5+n;        //总时长即为  最高楼层*（4+6）+ 不同层数*5+总人数 
	cout<<sum;                //输出 
	return 0;
}
```

