int read(){
	int abs=0;
    char c=getchar();
    while(!isdigit(c)) c=getchar();
	while(isdigit(c)){
    	abs=abs*10+c-'0';
        c=getchar();
    }
	return abs;
}//数据量过大，用读入优化
priority_queue<int> q;
int wait(int f){
	int t=5;//停靠层的基础值
	while(q.top()==f&&q.size()) t++,q.pop();//这一层还有人要出，则时间+1
	return t;
}//wait()函数中的处理保证了每一层不会被重复计算
int main(){
	int n=read();
	while(n--) q.push(read());
	int t=q.top()*10;//总时间的基础值
	while(q.size()) t+=wait(q.top());//叠加每一层的停靠时间
	printf("%d",t);
	return 0;
}
