#include<bits/stdc++.h>
using namespace std;
string a,s,e,ans[50];
int num,v[150],t;
void change(string &s)//将小写转为大写 
{
    int i;
    int len=s.size();
    for(i=0;i<len;i++)
    {
        if(s[i]>='a'&&s[i]<='z')
        s[i]-=32;
    }
}
void translate(string &s,int t)
{
    int len=s.size();
    for(int i=0;i<len;i++)
    {
        if(s[i]>='A'&&s[i]<='Z')
        {
            s[i]+=t;//有左移和右移两种移法，那t就有正负两种情况 
            if(s[i]>'Z') s[i]-=26; 
            if(s[i]<'A') s[i]+=26;//所以这里要有两个边界判断 
        }
    }
}
int f(string s)
{
    int i,len=s.size();
    for(i=0;i<len;i++)
    v[(int)s[i]]++;
    int maxn=0,j;
    for(i='A';i<='Z';i++)
    {
        if(v[i]>maxn)
        {
            maxn=v[i];
            j=i;
        }
    }
    int ans='E'-j;//"E"是翻译成的明文，求偏移量用"E"-某出现最多字母 
    return ans;
}
int main()
{
    while(cin>>s,getline(cin,a),getline(cin,a),cin>>e)
    {
        change(a);
        num++;
        if(num==1)//t根据第一句话来求
        t=f(a);
        translate(a,t);
