    }                    //求出现次数，这里开了个map数组
    for (char i = 'A';i <= 'Z';i++)
        if (_[i] > _[p])
            p = i;       //判断出现次数最多的
    t = 'E' - p;         //求t
    for (int i = 0;i < w.size();i++)
    {
        if (w[i] >= 'A' && w[i] <= 'Z')
        {
