        	    w[i] -= 32;
            if (w[i] >= 'A' && w[i] <= 'Z')
            {
                if (w[i] + t > 'Z')
                    cout << (char)(w[i] + t - 26);
                else if (w[i] + t < 'A')
                    cout << (char)(w[i] + t + 26);
                else
                    cout << (char)(w[i] + t);
