#include<iostream>
using namespace std; 
int n,m,x;
int a[107],b[107],c[107];
int f[1007][1007];//这里用的是二维，即把原来的一维改成了二维
int main()
{
	cin>>n>>m>>x;
	for(int i=1;i<=n;i++)
		cin>>a[i]>>b[i]>>c[i];
	for(int i=1;i<=n;i++)//不变
	for(int j=m;j>=b[i];j--)
	for(int k=x;k>=c[i];k--)//变成两重循环（因为两个代价嘛）
		f[j][k]=max(f[j][k],f[j-b[i]][k-c[i]]+a[i]);
        //这里也变成两种代价分别减去
	cout<<f[m][x];//相应变化
	return 0;
}
