```cpp
#include <cstdio>
#include <iostream>
using namespace std;
int mon[999],w[999],q[999];
int n,m,a;
int ans;
void dfs(int x,int mony,int qb,int wz)
{   
    ans=max(qb,ans);
    if(x==n+1) return;
    if(mony+mon[x]<=a&&wz+w[x]<=m)
    dfs(x+1,mony+mon[x],qb+q[x],wz+w[x]);
    dfs(x+1,mony,qb,wz);
}
int main()
{
    scanf("%d%d%d",&n,&m,&a);
    for(int i=1;i<=n;i++)
     scanf("%d%d%d",q+i,w+i,mon+i);
    dfs(1,0,0,0);
    printf("%d",ans);
    return 0;
} 
