	for (int i = 1; i <= end; i++)
		for (int j = 1; j <= end; j++)
			if (a[i][j] !=	0 && box[a[i][j]] == 0)
			{
				box[a[i][j]] = index;
				index++;
			}

	for (int i = 1; i <= end; i++)
		for (int j = 1; j <= end; j++)
			a[i][j] = box[a[i][j]];


	for (int i = 1; i <= end; i++)
	{
