#include<iostream>
#include<algorithm>
using namespace std;
const int MaxN=100005;
struct Node
{
    int id,x;
}a[MaxN];
inline bool cmp(Node i,Node j)
{
    return i.x<j.x;
}
inline int F(int s,int R)
{
    int L=1,Mid;
    while(L<=R)
    {
        Mid=(L+R)/2;
        if(a[Mid].x<s)
            L=Mid+1;
        else
            R=Mid-1;
    }
    if(a[L].x==s)return a[L].id;
    return 0;
}
int main()
{
    int N,i,M;
    scanf("%d",&N);
    for(i=1;i<=N;i++)
    {
        scanf("%d",&a[i].x);
        a[i].id=i;
    }
    sort(a+1,a+1+N,cmp);
    scanf("%d",&M);
    while(M--)
    {
        scanf("%d",&i);
        printf("%d\n",F(i,N));
    }
