{
	double n,x,xx;scanf("%lf%lf",&x,&n);
	double da=0;xx=x;
	for(double i=1;i<=min(n,10000000.0);i++)
	{
		da+=xx/i;
		xx*=x;
	}
	printf("%.4lf\n",da);
