double sum=0;//别写成int
double mi(double a,long long b){
	double ans=1;
	while(b!=0){
		if(b&1) ans=ans*a;
		a=a*a;
		b=b>>1;
	}
	return ans;
}//快速幂，用递归的也可以。

int main(){
