#include<iostream>
#include<algorithm>
#include<cmath>
using namespace std;
const double e = exp(1.000);

int gcd(int a, int b)
{
	return (b == 0) ? a : gcd(b, a % b);
}

double cc(int n, int x)
{
	return (double)x * log((double)n / x);
}

int calc(int n)
{
	int x1 = n / e, x2 = n / e + 1;
	int x = cc(n, x1) > cc(n, x2) ? x1 : x2;
	int p = gcd(n, x);
	x /= p;
	while (x % 2 == 0)
		x /= 2;
	while (x % 5 == 0)
		x /= 5;
	return (x == 1) ? -n : n;
}

int main()
{
	int a;
	cin >> a;
	int res = 0;
	for (int i = 5; i <= a; i++)
		res += calc(i);
	cout << res;
	return 0;
}
