#include<bits/stdc++.h>//万能库
using namespace std;
int n,m,k,r;//无需解释
int a[11];//要刷的题目需要的时间
int w[11];//作业所需时间
int c[11];//作业分值
int f[501] = {0};//表示用f[i]时间能够得到的分数 
int stt;//表示余剩下用来刷题的时间 
int stn = 0;//已经刷的个数
int main()
{
    ios_base::sync_with_stdio(0);//让cin变快 de 黑科技x1
    cin.tie(0);//让cin变快 de 黑科技x2
    cin>>n>>m>>k>>r;
    for(int i=1;i<=n;i++)
    {
        cin>>a[i];
    }
    sort(a+1,a+n+1);//尽量挑时间短的题目去做
    for(int i=1;i<=m;i++)
    {
        cin>>w[i];
    }
    for(int i=1;i<=m;i++)
    {
        cin>>c[i];
    }
    for(int i=1;i<=n;i++)
    {
        for(int j=r;j>=w[i];j--)//标准01背包写法
        {
            f[j] = max(f[j],f[j-w[i]] + c[i]);
        }
    }
    for(int i=1;i<=r;i++)
    {
        if(f[i] >= k)//可以及格了
        {
            stt = r - i;//已经写作业花去的时间不能刷题了
            break;
        }
    }
    for(int i=1;i<=n;i++)
    {
        stt -= a[i];//刷了一道题
        if(stt <= 0) break;//没时间了赶紧跑去学校
        stn++;//多刷了一道题，成就感++，rp--
    }
    cout<<stn<<endl;//输出刷题数量
    return 0;
}
```

