#include<iostream>
using namespace std;
int n,m,k,r;
int ti[1000],zuo[1000],shi[1000];//ti是“题目”，zuo是“作业”，shi是“时间”
int kk[1000],sum=0;//避免和“k”重复，所以很无耻的用了“kk”
void swap(int &a,int &b)//交换函数 
{
    int t;
    t=a; b=a; b=t;
}
void input()//输入
{
    cin>>n>>m>>k>>r;
    for(int i=1;i<=n;i++)
        cin>>ti[i];
    for(int i=1;i<=m;i++)
        cin>>zuo[i];
    for(int i=1; i<=m; i++)
        cin>>shi[i];
}
void work()//关键在这里！ 
{
    for(int i=1;i<=n;i++)//背包
    {
        for(int j=r;j>=zuo[i];j--)
        {
            if(kk[j]<kk[j-zuo[i]]+shi[i])
            {
                kk[j]=kk[j-zuo[i]]+shi[i];
            }
        }
    }
    for(int i=0;i<=r;i++)//大型搞事情环节
    {    
        if(kk[i]>=k)
        {
            r-=i;
            break;//炸炸炸
        }
        for(int i=1;i<n;i++)//弱鸡冒泡。这里不知为何用STL模板有毒，编译过不去。。。
        {
            for(int j=i+1;j<=n;j++)
            {
                if(zuo[i]<zuo[j])
                {
                    swap(zuo[i],zuo[j]);
                }
            }
        }
    }
    for(int i=1;i<=n;i++)//算一算！
    {
        if(r<ti[i])
            break;//之老是二话不说就炸。。。
        else
        {
            r-=ti[i];
            sum++;
        }
    }
}
void output()//输出喽 
{
    cout<<sum<<endl;
}
