    for(j=1;j<=m;j++) scanf("%d",&a[j]);//作业用时 
    for(j=1;j<=m;j++) scanf("%d",&b[j]);//对应分数 
    sort(s+1,s+n+1);  //现排序一下，方便后面好计算 
    for(i=1;i<=m;i++) //01背包 
    {
        for(j=k;j>=b[i];j--)
