#define INF 0x7fffffff  //因为存的是最小值一定要记得初始化一个很大的数
using namespace std;
ll n,f[205],a[205];  //f-走到当前位置x所需要的步数 
ll t[32];            //t-存2^k的值，因为我们后面要跳的步数一定是2^k
int main()
{
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
     scanf("%d",&a[i]);
    t[0]=1;
    for(int i=1;i<=31;i++) t[i]=t[i-1]*2;  //预处理 
    f[1]=0;
    for(int i=2;i<=n;i++) f[i]=INF;  //初始化f的值 
    for(int i=2;i<=n;i++)
     {
