int n,t,a[1000],ans[1000];
int dfs(int i)
{
	if(i==n)return 0;//边界
	if(ans[i])return ans[i];//记忆化
	ans[i]=n*n*a[i]+dfs(i+1);//单独打破这一层
	register int sum=a[i];//不用前缀和
	for(register int j=i+1;j<n;++j)
	{
		sum+=a[j];//加和
		if(a[i]+a[j]<=t)//满足条件
			ans[i]=min(ans[i],dfs(j+1)+(a[i]+a[j])*sum);//转移
	}
	return ans[i];
}
