			}
			if(j<=m-up[i])
			{
				F[i+1][j+up[i]][1]=min(F[i+1][j+up[i]][1],min(F[i][j][1],F[i][j][0])+1);
			}
			if(j>m-up[i]) F[i+1][m][1]=min(F[i+1][m][1],min(F[i][j][1],F[i][j][0])+1);
