## CODE
```cpp
#include<bits/stdc++.h>
#define Maxn 10002
#define Maxm 1002
#define inf 400000002 

using namespace std;

int Map[Maxn][Maxm];
int jump[Maxn]; int down[Maxn];
int dp[Maxn][Maxm];
int lim[Maxn];

int main(){

    int n,m,k; cin>>n>>m>>k;

    for(int i=0;i<n;i++) cin>>jump[i]>>down[i];

    for(int i=1;i<=k;i++){
        int x,up,low; cin>>x>>low>>up;
        lim[x]++;
        for(int j=m;j>=up;j--)
            Map[x][j]=1;
        for(int j=0;j<=low;j++)
            Map[x][j]=1;
    }


    for(int j=0;j<=m;j++)
    	if(Map[0][j]==1)
    		dp[0][j]=inf;
    //dp[0][0]=inf;
    int num=0;

    for(int i=1;i<=n;i++){
    	//计算过了的柱子 
    	if(lim[i-1]!=0)
			num++;
    	for(int j=0;j<=m;j++)
    		dp[i][j]=inf;
    	//跳 
		for(int j=jump[i-1];j<=m;j++){
			dp[i][j]=min(dp[i-1][j-jump[i-1]],dp[i][j-jump[i-1]])+1;
		}
		for(int j=max(0,m-jump[i-1]);j<=m;j++){
				dp[i][m]=min(dp[i][m],dp[i-1][j]+1);
				dp[i][m]=min(dp[i][m],dp[i][j]+1);
		}	
		//降 
		for(int j=1;j+down[i-1]<=m;j++){
			if(dp[i-1][j+down[i-1]]<inf)
				dp[i][j]=min(dp[i][j],dp[i-1][j+down[i-1]]);
		}

		for(int j=1;j<=m;j++){
			if(Map[i][j]==1)
				dp[i][j]=inf;
			if(dp[i][j]>inf)
				dp[i][j]=inf;
		}
		dp[i][0]=inf;
		//还能走否 
		bool flag=true;
		for(int j=1;j<=m;j++)
			if(dp[i][j]!=inf){
				flag=false;
				break;
			}
		if(flag){
			cout<<0<<endl<<num<<endl;
			return 0;
		}
	}


    int Min=inf;
    for(int i=1;i<=m;i++)
    	Min=min(Min,dp[n][i]);

    cout<<1<<endl<<Min;

    return 0;
}
