完整代码

```cpp
#include <stdio.h>
#include <iostream>
#define maxn 10005
using namespace std;
int n,m,k;
int up[maxn],down[maxn];
struct pipe {
    int lower,upper;
    int x;
} pipes[maxn]= {{0,0,0}};
int dp[maxn][maxn];
int main() {
    cin>>n>>m>>k;
    for(int i = 0; i<n; i++) {
        cin>>up[i]>>down[i];
    }
    for(int i = 1; i<=k; i++) {
        int x,lower,upper;
        cin>>x>>lower>>upper;
        pipes[x].lower = lower;
        pipes[x].upper = upper;
    }

    for(int i = 0; i<=m; i++)for(int j = 0; j<=n; j++)dp[j][i] = 1e9;
    for(int i = 1; i<=m; i++)dp[0][i] = 0;

    for(int i = 0; i<=n; i++) {
        int hasntmove = true;
        for(int j = m; j>=1; j--) {

            if(dp[i][j]!=1e9) {

                hasntmove = false;
                if(pipes[i].upper) {
                    if(j>=pipes[i].upper||j<=pipes[i].lower) {
                        continue;
                    }
                }

                if(j==m) {
                    dp[i+1][j] = min(dp[i+1][j],dp[i][j]+1);
                    dp[i+1][j-down[i]] = min(dp[i+1][j-down[i]],dp[i][j]);
                } else {
                    if(up[i])
                    for(int d = 1; d<=m/up[i]+1; d++) {
                        int uppp = j+up[i]*d;
                        if(j+(up[i])*(d-1)>m) break;
                        if(uppp>m) uppp = m;
                        if(pipes[i+1].upper) {
                            if(uppp<pipes[i+1].upper&&uppp>pipes[i+1].lower) {
                                if(dp[i+1][uppp]<dp[i][j]+d) break;
                                else
                                    dp[i+1][uppp] = min(dp[i+1][uppp],dp[i][j]+d);

                            }
                        } else {
                            if(dp[i+1][uppp]<dp[i][j]+d) break;
                            else
                                dp[i+1][uppp] = min(dp[i+1][uppp],dp[i][j]+d);

                        }
                    }

                    //down
                    if(j-down[i]>0) {
                        int uppp = j - down[i];
                        if(pipes[i+1].upper) {
                            if(uppp<pipes[i+1].upper&&uppp>pipes[i+1].lower) {
                                dp[i+1][uppp] = min(dp[i+1][uppp],dp[i][j]);

                            }
                        } else {
                            dp[i+1][uppp] = min(dp[i+1][uppp],dp[i][j]);

                        }
                    }

                }

            }


        }
        if(hasntmove) {
            cout<<0<<endl;
            int ans1 = 0;
            for(int mm = 0; mm<=i; mm++) {
                if(pipes[mm].upper) {
                    ans1++;
                }
            }
            cout<<--ans1;
            return 0;
        }
    }

    int ans = 1e9;
    for(int i = 1; i<m; i++) {
        ans = min(ans,dp[n][i]);
    }
    cout<<1<<endl;
    cout<<ans;
    return 0;
}
