int n,tot,len,hm[5005];
string s;

void jj()
{
	memset(hm,0,sizeof(hm));
	tot = 0;len = s.length();
	for(int i = 0; i < len; i++) if(s[i] == '1') tot += (i+1);
	if(len == n)
	{
		if(tot % (n+1) == 0) cout<<s<<endl;//不变化的情况
		else//把一个1变回0
		{
