					s[i] = '0';
					cout<<s<<endl;
					return;
				}
			printf("-1\n");
		}
	}
	else
	{
		for(int i = len-1; i >= 0; i--) hm[i] = hm[i+1] + (s[i] == '1');//hm[i]表示i(含)以后这串有几个1
		if(n > len)
		{
			for(int i = 0; i <= len; i++)
				if((tot+hm[i])%(n+1) == 0)
				{
					s.insert(i,1,'0');
					cout<<s<<endl;return;
				}
			for(int i = 0; i <= len; i++)
				if((tot+hm[i]+i+1)%(n+1) == 0)
				{
					s.insert(i,1,'1');
					cout<<s<<endl;return;
				}
		}
		else
		{
			for(int i = 0; i < len; i++)
			{
				if(s[i] == '1' && (tot-hm[i]-i)%(n+1) == 0)
				{
					s.erase(i,1);
					cout<<s<<endl;return;
				}
				if(s[i] == '0' && (tot - hm[i]) % (n+1) == 0)
				{
					s.erase(i,1);
					cout<<s<<endl;return;
				}
			}
		}
		printf("-1\n");
	}
}

