bool vis[siz];
int main(){

    scanf("%s",a);
    la=strlen(a);
    for(int i=0;i<la;++i) 
      if((sta[top][0]=='['&&a[i]==']')||(sta[top][0]=='('&&a[i]==')')) vis[sta[top--][1]]=vis[i]=1;
      else sta[++top][0]=a[i],sta[top][1]=i;
    for(int i=0;i<la;++i)
