代码:
```cpp
// luogu-judger-enable-o2
#include <iostream>
#include <cstdlib>
#include <cstdio>
#include <cstring>

using namespace std;
const int MAXN = 1005;
typedef long long ll;
int n,m,map[MAXN][MAXN],maxa[MAXN],stack[MAXN],tmp[MAXN],top = 0;

int main()
{
    char c;
    cin >> n >> m;
    for(int i = 1; i<=n; i++)
        for(int j = 1; j<=m; j++)
        {
            cin >> c;
            map[i][j] = c=='*';
        }
    ll ans = (ll)n*(ll)m*(ll)(n+1)*(ll)(m+1)/4;
    for(int i = 1; i<=n; i++)
    {
    	memset(stack,0,sizeof(stack));
    	memset(tmp,0,sizeof(tmp));
    	top = 0;
        for(int j = 1; j<=m; j++)
        {
            if(map[i][j]) maxa[j] = i;
            while(top>0&&maxa[stack[top]]<=maxa[j]) top--;
            ans -= tmp[j] = tmp[stack[top]]+maxa[j]*(j-stack[top]);
            stack[++top] = j;
        }
    }
    cout << ans << endl;
    return 0;
}
```

