    for(int i=0;i<t;i++){//开始进行加法竖式模拟
        c[i]+=f(a[i])+f(b[i]);//c是暂时储存结果的数组
        if(c[i]>=n)c[i]-=n,c[i+1]++;//进位
        k[i]=f_(c[i]);//把结果转换进新数组
    }
    if(c[t]==1)cout<<"1";//如果进位进到a,b数组的长度之外，进行特判
