      zh[i]:=ord(s[zh[0]-i+1])-ord('a')+10;  //如果是字母，比如a就转成10
    if (ord(s[zh[0]-i+1])>=ord('0'))and(ord(s[zh[0]-i+1])<=ord('9')) then
      zh[i]:=ord(s[zh[0]-i+1])-ord('0');  //数字的情况
  end;
end;
