{有过程}
var
 n,i:longint;
 s:ansistring;
 typ:char;

procedure work_1(s:string);//加法
var
 m,n,t:longint;
begin
 val(copy(s,1,pos(' ',s)-1),m);//第一个运算数
 val(copy(s,pos(' ',s)+1,length(s)),n);//第二个运算数
 writeln(m,'+',n,'=',m+n);
 t:=length(s);
 str(m+n,s);
 writeln(t+length(s)+1);
 typ:='a'
end;

procedure work_2(s:string);//减法
var
 m,n,t:longint;
begin
 val(copy(s,1,pos(' ',s)-1),m);
 val(copy(s,pos(' ',s)+1,length(s)),n);
 writeln(m,'-',n,'=',m-n);
 t:=length(s);
 str(m-n,s);
 writeln(t+length(s)+1);
 typ:='b'
end;

procedure work_3(s:string);//乘法
var
 m,n,t:longint;
begin
 val(copy(s,1,pos(' ',s)-1),m);
 val(copy(s,pos(' ',s)+1,length(s)),n);
 writeln(m,'*',n,'=',m*n);
 t:=length(s);
 str(m*n,s);
 writeln(t+length(s)+1);
 typ:='c'
end;

begin
 readln(n);
 for i:=1 to n do
 begin
  readln(s);
  if s[1] in ['a'..'c'] then
    case s[1] of
     'a':work_1(copy(s,3,length(s)));
     'b':work_2(copy(s,3,length(s)));
     'c':work_3(copy(s,3,length(s)))
    end
   else
    case typ of
     'a':work_1(s);
     'b':work_2(s);
     'c':work_3(s)
    end
 end
end.

