    #define ll long long
    inline ll gcd(ll a,ll b)
    {return b?gcd(b,a%b):a;}
    inline ll lca(ll a,ll b)
    {return a*b/gcd(a,b);}
    int n;bool in[110];
    struct tp
    {
        ll lx,rx,lt,rt;
    }t[110];
    ll dg(int w)
    {
        ll lt=t[w].lt,rt=t[w].rt,lx=t[w].lx,rx=t[w].rx;
        if(!lt&&!rt){return lx+rx;}
        ll l=(lt?dg(lt):rx);
        ll r=(rt?dg(rt):lx);
        l=lca(l,rx),r=lca(r,lx);
        ll lb=l/rx,rb=r/lx;
        ll b=lca(lb,rb);
