    }
    int main()
    {
        scanf("%d",&n);
        for(int i=1;i<=n;++i)
        {
            scanf("%d %d %d %d",&t[i].lx,&t[i].rx,&t[i].lt,&t[i].rt);
            int g=gcd(t[i].lx,t[i].rx);
            t[i].lx/=g,t[i].rx/=g;
            in[t[i].lt]=in[t[i].rt]=1;
        }
        int w=0;
        for(int i=1;i<=n;++i)
            if(!in[i])
            {
                w=i;break;
            }
        printf("%lld",dg(w));
        return 0;
    }

