       p[i]=read(),q[i]=read(),l[i]=read(),r[i]=read(),in[l[i]]=in[r[i]]=1;   //记录入度
    rep(i,1,n) if(!in[i]){ root=i;break; }  //找根
    printf("%lld\n",Build(root));
