#include <bits/stdc++.h>
using namespace std;

const int mod=1e9+7;

long long n,k;

struct juz{
	long long mp[101][101];

	juz(){
		memset(mp,0,sizeof(mp));
	}

	void bu()
	{
		for(int i=1;i<=2;++i)
		{
			mp[i][i]=1;//单位矩阵
		}
	}	
};


juz operator *(const juz &x,const juz &y)//重载运算符，这样在主函数里更方便
{
	juz z;
	for(int k=1;k<=2;++k)
	{
		for(int i=1;i<=2;++i)
		{
			for(int j=1;j<=2;++j)
			{
				z.mp[i][j]=(z.mp[i][j]+x.mp[i][k]*y.mp[k][j]%mod)%mod;//矩阵乘法
			}
		}
	}
	return z;
}
juz q;

int main() {

