```cpp
#include<bits/stdc++.h>
#define ULL unsigned long long
using namespace std;
char tmp[11];
char s[30000010];
int q[30000010];
ULL k,p=1000000007 ,a[3],b[3][3],result[3][3];
void matrix_multiply_result ()
{
	ULL c[3][3];
    memset (c,0,sizeof (c));
    for (int i=1;i<=2;i++)
    	for (int j=1;j<=2;j++)
    		for (int k=1;k<=2;k++)
				c[i][k]=(c[i][k]+result[i][j]*b[j][k])%p;
	for (int i=1;i<=2;i++)
		for (int j=1;j<=2;j++)
			result[i][j]=c[i][j];
}
void matrix_multiply_b ()
{
    ULL c[3][3];
    memset (c,0,sizeof (c));
    for (int i=1;i<=2;i++)
    	for (int j=1;j<=2;j++)
    		for (int k=1;k<=2;k++)
				c[i][k]=(c[i][k]+b[i][j]*b[j][k])%p;
	for (int i=1;i<=2;i++)
		for (int j=1;j<=2;j++)
			b[i][j]=c[i][j];
}
void matrix_multiply_a ()
{
    ULL c[3];
    memset (c,0,sizeof (c));
    for (int i=1;i<=2;i++)
    	for (int j=1;j<=2;j++)
				c[i]=(c[i]+a[j]*b[i][j])%p;
	for (int i=1;i<=2;i++)
		a[i]=c[i];
}
int main()
{
	a[1]=1,a[2]=1;
	b[1][1]=b[1][2]=b[2][1]=1;
	b[2][2]=0;
	result[1][1]=1;result[1][2]=0;
    result[2][1]=0;result[2][2]=1;
    scanf ("%llu%llu",&k);
