struct mat{long long a[2][2];};   

mat mat_mul(mat x,mat y)           //实现两个矩阵相乘，返回的还是一个矩阵。   
{   
    mat res;                       //用来表示得到的新的矩阵；   
    memset(res.a,0,sizeof(res.a));   
    for(int i=0;i<2;i++)   
        for(int j=0;j<2;j++)   
           for(int k=0;k<2;k++)   
                res.a[i][j]=(res.a[i][j]+(x.a[i][k]*y.a[k][j])%MOD)%MOD;   
    return res;   
}   

mat initi(mat a,mat ans,long long b)
{
    while(b>0)
    {
        if(b&1) 
          ans = mat_mul(ans,a);
        b = b >> 1;
        a = mat_mul(a,a);
    }
    return ans;        
}

long long n;
int main()
{
    mat a,b;

    a.a[0][0]=1;
    a.a[0][1]=1;
    a.a[1][0]=1;
    a.a[1][1]=0;

    b.a[0][0]=1;
    b.a[0][1]=1;
    b.a[1][0]=1;
    b.a[1][1]=0;

    n=red();
    b=initi(a,b,n);
    cout<<b.a[1][1];

    return 0;
}
