```cpp
inline matrix qkpow(matrix A,ll n)
{
	matrix res;
	res.n = res.m = 2,res.c[1][1] = res.c[2][2] = 1;
	res.c[1][2] = res.c[2][1] = 0;
	while(n)
	{
		if(n & 1) res = res * A;
		n >>= 1;
		A = A * A;
	}
	return res;
}
