```cpp
#include<bits/stdc++.h>
#define ll long long
using namespace std;
struct jz
{
    ll x[110][110];
};
jz a,ans,A,r;
ll n,m,t;
jz cheng(jz A,jz B,ll mod)//两个矩阵相乘 
{
    jz c;
    for(int i=1;i<=t;i++)
    {
   		for(int j=1;j<=t;j++)
        {
            c.x[i][j]=0;
            for(int k=1;k<=t;k++)
            {
                c.x[i][j]+=A.x[i][k]*B.x[k][j]%mod;
                c.x[i][j]=c.x[i][j]%mod;
            }
        }
	}
    return c;
}
jz danweizhen()//得到一个单位阵 
{
    jz A;
    for(int i=1;i<=t;i++)
        for(int j=1;j<=t;j++)
        {
            if(i==j)
                A.x[i][j]=1;
            else
                A.x[i][j]=0;
        }
    return A;
}
jz kuaisumi(jz A,ll n,ll mod)//求A^n%mod
{
    jz ans=danweizhen();
    jz T=A;
    for(;n;n/=2)
    {
        if(n&1)
            ans=cheng(ans,T,mod);
        T=cheng(T,T,mod);
    }
    return ans;
}
int main()
