long long n,m;
long long a[105][105],b[105][105],c[105][105];
int main() {
	scanf("%lld",&m);
	a[1][1]=a[1][2]=a[2][1]=1;a[2][2]=0;
	n=2;for(int i=1;i<=n;i++)
	for(int j=1;j<=n;j++)b[i][j]=a[i][j];
	m-=2;
	while(m){
		if(m%2==1){
			for(int i=1;i<=n;i++)
				for(int j=1;j<=n;j++)c[i][j]=0;
			for(int k=1;k<=n;k++)
				for(int i=1;i<=n;i++)
					for(int j=1;j<=n;j++)
						c[i][j]=(c[i][j]+a[i][k]*b[k][j])%Mod;
			for(int i=1;i<=n;i++)
				for(int j=1;j<=n;j++)
