		for(int k=1;k<=n;k++)
			for(int i=1;i<=n;i++)
				for(int j=1;j<=n;j++)
					a[i][j]=(a[i][j]+c[i][k]*c[k][j])%Mod;
		m/=2;		
	}
	printf("%lld\n",b[1][1]%Mod);
