						{
							a[j].w += a[i].w;//合并
							a[i].w = -1;//标记
						}
				}
		}
	for(int i = 1; i <= n; i++)
		{
			if(a[i].w != -1)//合并到前面的就不考虑
				{
					int p;
					bool bo = false;
					if (a[i].w % a[i].c > 0)//每放满一个格子，就算一个物件，占用一个空间，从而达到转01背包的作用
						{
							p = a[i].w / a[i].c + 1;
							bo = 1;//标记，最后一个没放满
						}
					else
						p = a[i].w / a[i].c;
					for(int j = 1; j <= p; j++)//计算价值									{
