								{
									b[++t].v = a[i].v * (a[i].w % a[i].c);//如果最后一个没放满，要特判
									b[t].w = 1;//算占用一个空间
								}
							else
								{
									b[++t].v = a[i].v * a[i].c;
									b[t].w = 1;
								}
						}
				}
		}
	for(int i = 1; i <= t; i++)
		for(int j = x; j >= b[i].w; j--)
			f[j] = max(f[j] , f[j - b[i].w] + b[i].v);//华丽的01背包模板
	cout << f[x];//输出
}
