	for(set<string>::iterator it=st.begin();it!=st.end();it++){//iterator遍历set
		while(mp[*it].own){//只要还有
			v.push_back(min(mp[*it].own,mp[*it].mx)*mp[*it].cost);//就往里面装
			//这里注意要取min(数量,上限)
			mp[*it].own=max(0,mp[*it].own-mp[*it].mx);//减掉
		}
