	sort(v.begin(),v.end());//懒人排序
	int END=max(0,(int)v.size()-space);
	//这里千万不要直接把v.size()-space写到下一行的END里面去
	for(int i=v.size()-1;i>=END;i--){
		ans+=v[i];
	}
	cout<<ans<<endl;//输出
