int main()
{
    int i,n,a;
    scanf("%d",&n);
    for(i=1;i<=n;i++)
    {
        scanf("%d",&a);
        if(i==1)
        {
            dp[1][0]=100;
            dp[1][1]=a;
        }
        else
        {
            dp[i][0] = max( dp[i-1][0], 1.0*dp[i-1][1]/a*100 );
            dp[i][1] = max( dp[i-1][1], 1.0*dp[i-1][0]*a/100 );
        }
    }
    double ans1=dp[n][0],ans2=1.0*dp[n][1]/a*100;
    double ans=max(ans1,ans2);
    printf("%.2f\n",ans);
    return 0;
}
```
