using namespace std;
int n,h[100005],i,q[100005],top,tmp,ans;
int main(){
    scanf("%d",&n);
    for(i=1;i<=n;i++)
    {
        scanf("%d",&h[i]);
        tmp=q[top];
        while(h[i]<q[top])
            top--;//因为几个连续高度的部分可合并，所以可以出栈
        if(tmp>h[i])
            ans+=tmp-h[i];//计算如果把山峰凸起部分铲平，要多少次
        q[++top]=h[i];
    }
    printf("%d",ans+q[top]);
}

```

